import { useEffect, useState } from "react";
import ApiService from "../services/ApiService";
import { useParams } from "react-router-dom";
import Review from "./Review";

function DisplayMovies(props) {
  let image = "images/" + props.movie.movieName + ".jpg";
  //console.log(image);
  let apiService = new ApiService();
  let { id } = useParams();

  let [reviews, setReviews] = useState([]);
  useEffect(() => {
    apiService.getMovieById(id).then(
      (res) => {
        console.log("Response is ready...");
        console.log(res.data);
        setReviews(res.data);
      },
      (err) => { console.log("Error: " + err) }
    );
  }, []);

  let delMovie = (mid) => {
    apiService.deleteMovie(mid).then((res) => alert("Deleted"))
  }
  
  return <div class="container">
    <div class="card">
      <i class="remIcon bi bi-trash" onClick={() => { delMovie(props.movie.movieId) }} align="right"></i>
      <div class="card-body">
        <img src={image} width="200px" align="left"></img>
        <br></br>
        <h5 class="card-title">Movie Name: {props.movie.movieName}</h5>
        <p class="card-text">Movie Id: {props.movie.movieId}</p>
        <p class="card-text">Movie genre: {props.movie.genre}</p>
        <p class="card-text">Movie language: {props.movie.language}</p>
        <br></br>
        <a href={"/addMovie/" + props.movie.movieId}><i class="bi bi-pencil-square"></i></a>
        <br></br>
        <br></br>
        <a href={"/movies/reviews/" + props.movie.movieId} class="btn btn-primary">Review</a>
      </div>
    </div>

    {
      //reviews.map(r =><Review review={r} />)
    }

{
  reviews.map(r=>
  {
    <div class="card" >
            <img src="..." class="card-img-top" alt="..."></img>
            <div class="card-body">
                <h5 class="card-title">{r.reviewerName}</h5>
                <p class="card-text">Some quick example text to build on the card title and make up the bulk of the card's content.</p>
            </div>
            <ul class="list-group list-group-flush">
                <li class="list-group-item">An item</li>
                <li class="list-group-item">A second item</li>
                <li class="list-group-item">A third item</li>
            </ul>
            <div class="card-body">
                <a href="#" class="card-link">Card link</a>
                <a href="#" class="card-link">Another link</a>
            </div>
        </div>
  })
}
  </div>
}
export default DisplayMovies;
