import { useNavigate, useParams } from "react-router-dom";
import ApiService from "../services/ApiService";
import { useEffect, useState } from "react";

function Review() {
    let apiService = new ApiService();
    let { ids } = useParams();
    let [movie, setMovie] = useState({});
    let [reviews, setReviews] = useState({});
    let navigate = useNavigate();
    let img = "/images/" + movie.movieName + ".jpg";
    useEffect(() => {
        apiService.getMovieById(ids).then(
            (res) => {
                setMovie(res.data);
            },
            (err) => { console.log("Error: " + err) }
        );
    }, [])

    useEffect(() => {
        console.log(ids);
        apiService.getReviewById(ids).then(
            (res) => {
                console.log("Response is ready...");
                console.log(res.data);
                setReviews(res.data);
                console.log(reviews);
            },
            (err) => { console.log("Error: " + err) }
        );
    }, []);


    let reviewerNameHandler = (evt)=>{
        setReviews((prevState)=>{return {...prevState, reviewerName:evt.target.value}})
        };


    let reviewHandler = (evt) =>{
        setReviews((prevState)=>{return {...prevState, review:evt.target.value}});
    }

    let ratingHandler = (evt) =>{
        setReviews((prevState)=>{return {...prevState, rating:evt.target.value}});
    }

    let submitHandler = (evt)=>{
        evt.preventDefault();
        apiService.addNewReview(reviews,ids).then(
            (res)=>{
                alert("Review Added")
                navigate("/")
            }
        );
    }

    return <div>
        <h1>Review</h1>
        <img src={img} alt="" width="150px" height="200px"></img>

        <div class="card" >

            <div class="card-body">
                <h5 class="card-title">{movie.movieName} </h5>
            </div>
            <table class="table table-striped  table-hover">
                <thead>
                    <tr>
                        <th>Reviewer Id</th>
                        <th>Reviewer Name</th>
                        <th>Review</th>
                        <th>Rating</th>
                    </tr>
                </thead>
          
        {reviews.map(r =>
                <tbody>
                    <tr>
                        <td>{r.reviewId}</td>
                        <td>{r.reviewerName}</td>
                        <td>{r.review}</td>
                        <td>{r.rating}</td>
                    </tr>
                    </tbody>
          
        )}
          </table>
           </div>

           <h4>Add Review</h4>
           <div>
           <form onSubmit={submitHandler}> 
            <label class="form-label">Enter Reviewer Name:</label>
            <input type="text" class="form-control" onChange={reviewerNameHandler} value={reviews.reviewerName}></input>
            <br></br>
            <label class="form-label">Enter review:</label>
            <input type="text" class="form-control" onChange={reviewHandler} value={reviews.review}></input>
            <br></br>
            <label class="form-label">Select rating:</label>
            <select class="form-control" onChange={ratingHandler} value={reviews.rating}>
                <option></option>
                <option>1</option>
                <option>2</option>
                <option>3</option>
                <option>4</option>
                <option>5</option>
            </select>
            <br></br>
            <button type="submit" class="btn btn-primary mb-2" >Add Review</button>
        </form>
           </div>
    </div>
}
export default Review;
