import { useParams } from "react-router-dom";
import ApiService from "../services/ApiService";
import { useEffect, useState } from "react";

function Review() {
    let apiService = new ApiService();
    let { ids } = useParams();
    let [movie, setMovie] = useState({});
    let [reviews, setReviews] = useState([]);
    useEffect(() => {
        console.log(ids);
        apiService.getReviewById(ids).then(
            (res) => {
                console.log("Response is ready...");
                console.log(res.data);
                setReviews(res.data);
                console.log(reviews);
            },
            (err) => { console.log("Error: " + err) }
        );
    }, []);
    
    return <div>
        <h1>Review</h1>
        <img src={"images/"+reviews.movieName+".jpg"} class="card-img-top" alt="" width="200px"></img>

        {reviews.map(r => <div class="card" >

            <div class="card-body">
                <h5 class="card-title">{r.movie.movieName} </h5>
            </div>
            <table class="table table-striped  table-hover" >
                <thead>
                    <tr>
                        <th>Reviewer Id</th>
                        <th>Reviewer Name</th>
                        <th>Review</th>
                        <th>Rating</th>
                    </tr>
                </thead>
            
                <thead>
                    <tr>
                    <td>{r.reviewId}</td>
                    <td>{r.reviewerName}</td>
                    <td>{r.review}</td>
                    <td>{r.rating}</td>
                    </tr>
                </thead>
            </table>

            
        </div>)}
    </div>
}
export default Review;
