import { useParams } from "react-router-dom";
import ApiService from "../services/ApiService";
import { useEffect, useState } from "react";

function Review() {
    let apiService = new ApiService();
    let { ids } = useParams();

    let [reviews, setReviews] = useState([]);
    useEffect(() => {
        console.log(ids);

        apiService.getReviewById(ids).then(
            (res) => {
                console.log("Response is ready...");
                console.log(res.data);
                setReviews(res.data);
                console.log(reviews);
            },
            (err) => { console.log("Error: " + err) }
        );
    }, []);

    return <div>
        <h1>Review</h1>
        <img src="" class="card-img-top" alt=""></img>

            {reviews.map(r => <div class="card" >
                <div class="card-body">
                    <h5 class="card-title">{r.movie.movieName} </h5>
                </div>
                <table align="center">

                <tr><p class="card-text">Movie Id: {r.movie.movieId}</p></tr>
                <tr>Reviewer Name: {r.reviewerName}</tr>
                <tr>Review: {r.review}</tr>
                <tr>Rating: {r.rating}</tr>
                </table>

                <div class="card-body">
                    <a href="#" class="card-link">Card link</a>
                    <a href="#" class="card-link">Another link</a>
                </div>
            </div>)}
    </div>
}
export default Review;
