import { useState } from "react";
import ApiService from "../services/ApiService";

function AddMovie() {
    let apiService = new ApiService();

    let [movie, setMovie] = useState({});

    let movieIdHandler = (evt) => {
        movie.movieId = evt.target.value;
        console.log(movie.movieId)
    }

    let movieNameHandler = (evt) => {
        movie.movieName = evt.target.value;
    }

    let movieGenreHandler = (evt) => {
        movie.genre = evt.target.value;
    }

    let movieLanguageHandler = (evt) => {
        movie.language = evt.target.value;
    }

    let submitHandler = (evt) => {
        //to avoid refreshing the page while submitting
        evt.preventDefault();
        apiService.addNewMovies(movie).then(
            (res) => console.log("Added...")
        );
    }

    return <div class="container">
        <h1>Add Movie</h1>
        <br></br>
        <form onSubmit={submitHandler}>
            <label class="form-label">Enter Movie ID:</label>
            <input type="text" class="form-control" onChange={movieIdHandler}></input>
            <br></br>
            <label class="form-label">Enter Movie Name:</label>
            <input type="text" class="form-control" onChange={movieNameHandler}></input>
            <br></br>
            <label class="form-label">Enter Genre:</label>
            <select class="form-control" onChange={movieGenreHandler}>
                <option></option>
                <option>Comedy</option>
                <option>Drama</option>
                <option>Action</option>
                <option>Romance</option>
                <option>Thriller</option>
            </select>
            <br></br>
            <label class="form-label">Enter Language:</label>
            <select class="form-control" onChange={movieLanguageHandler}>
                <option></option>
                <option>English</option>
                <option>Hindi</option>
                <option>Bengali</option>
                <option>Kannada</option>
                <option>Tamil</option>
            </select>
            <br></br>
            <button type="submit" class="btn btn-primary mb-2"  >Add Movie</button>
        </form>
    </div>
}
export default AddMovie;
