import axios from "axios";
const movieURL="http://localhost:9999/movies";
const reviewURL = "http://localhost:9999/movies/reviews/";
class ApiService{
    getAllMovies(){
         //Promise object .....
        return axios.get(movieURL);
    }
    getAllReviews(props){
        return axios.get(reviewURL+"{props.m.movieId}");
    }
    
}
export default ApiService;
