import { useParams } from "react-router-dom";
import ApiService from "../services/ApiService";
import { useEffect, useState } from "react";

function Review() {
    let apiService = new ApiService();
    let { ids } = useParams();

    let [reviews, setReviews] = useState([]);
    useEffect(() => {
        console.log(ids);

        apiService.getReviewById(ids).then(
            (res) => {
                console.log("Response is ready...");
                console.log(res.data);
                setReviews(res.data);
                console.log(reviews);
            },
            (err) => { console.log("Error: " + err) }
        );
    }, []);

    return <div>
        <h1>Review</h1>
        <img src="" class="card-img-top" alt=""></img>   

        <table> 
            <thead>
                <tr>
            <th>Movie ID</th>
            <th>Reviewer Name</th>
            <th>Review</th>
            <th>Rating</th>
            </tr>
            </thead>
        </table>
        <table>
        {reviews.map(r=><div class="card" >
            <div class="card-body">
                <h5 class="card-title">{r.movie.movieName} </h5>
                <p class="card-text">Movie Id: {r.movie.movieId}</p>
            </div>
            <ul class="list-group list-group-flush">
                <li class="list-group-item">Reviewer Name: {r.reviewerName}</li>
                <li class="list-group-item">Review: {r.review}</li>
                <li class="list-group-item">Rating: {r.rating}</li>
            </ul>
            <div class="card-body">
                <a href="#" class="card-link">Card link</a>
                <a href="#" class="card-link">Another link</a>
            </div>
        </div>)}
        </table>
        
    </div>
}
export default Review;
