    //useSelector ==> consume the data from the store
    const movies=useSelector(state=>state.movies);
    console.log("---------------------")
console.log("---"+movies)
console.log(movies)

    useEffect( ()=>{

      dispatch(getAllMovies()); 

    },[] );
