import Steps from "./Steps";

function Ingredient(props) {
    return (
        <div class="container">
            <p>{props.ingredient.name}</p>
            {
                props.ingredient.name.map(a=> <Steps s = {a}/>)
            }
        </div>
    )
}
export default Ingredient;
